import Link from 'next/link'
import Layout from '../components/layout'
import axios from 'axios'

const IndexPage = ({data}) => (
  <Layout>
    <div className="doc-page">
      <p><strong>civdocs.us</strong> makes it easy to read the founding documents of the United States of America. This site consumes data from <strong>api.civdocs.us</strong>, an open API meant to make these documents more widely available online. If you think a document should be added to the database, please open an issue on <a href="#" target="_blank">Github</a>.</p>
      <ul className="index-list">
        {data.map((doc, index) => {
          const {title} = doc;
          const url = `/docs/${title.toLowerCase().replace(' ', '-')}`;
          return <li key="{index}">
            <Link href={url}>
              <a>{title}</a>
            </Link>
          </li>
        })}
      </ul>
    </div>
  </Layout>
)

export async function getServerSideProps(ctx) {
  const req = await axios.get(`http://localhost:7687/docs/`);
  const data = req.data;
  return {
    props: {
      data,
    },
  };
}

export default IndexPage
