import React, { ReactNode } from 'react'
import Link from 'next/link'

const Content = (content = []) => content.map((paragraph, index) => <p key={index}>{paragraph}</p>)

const DocBody = ({doc: {author, date, title, content, subtitle, prev_section_index, next_section_index}, documentParam}) => (
  <>
    <nav className="doc__nav">
      <ul className="list-inline text-align-right margin-0">
        {prev_section_index ? <li><a href={`/docs/${documentParam}/${prev_section_index}`} className="color-link">prev</a></li> : null}
        <li><a href={`/docs/${documentParam}`} className="color-link">index</a></li>
        {next_section_index ? <li><a href={`/docs/${documentParam}/${next_section_index}`} className="color-link">next</a></li> : null}
      </ul>
    </nav>
    <div className="doc__metadata">
      <h1>{title}</h1>
      <h3>{subtitle}</h3>
      {author ? <p>{author}</p> : null}
      {date ? <p>{date}</p> : null}
    </div>
    <hr />
    <div className="doc__content">
      {Content(content)}
    </div>
    <nav className="doc__nav doc__nav-bottom">
      <ul className="list-inline text-align-right margin-0">
        {prev_section_index ? <li><a href={`/docs/${documentParam}/${prev_section_index}`} className="color-link">prev</a></li> : null}
        <li><a href={`/docs/${documentParam}`} className="color-link">index</a></li>
        {next_section_index ? <li><a href={`/docs/${documentParam}/${next_section_index}`} className="color-link">next</a></li> : null}
      </ul>
    </nav>
  </>
);

const Doc = ({doc, documentParam}) => {
  if (doc.content && doc.content.length) {
    return (
      <div className="doc">
        {DocBody({doc, documentParam})}
      </div>
    );
  } else {
    return (
      <div className="doc-not-found">
        <p>Requested document section does not exist.</p>
      </div>
    );
  }
}

export default Doc
