import Link from 'next/link'
import Layout from '../../../components/layout'
import Doc from '../../../components/doc'
import axios from 'axios'

const DocSectionIndex = (urlPrefix, sectionsTotal) => {
  let sections = [];

  for (let i = 1; i <= sectionsTotal; i++) {
    sections.push({
      url: `${urlPrefix}/${i}`,
      title: i,
    });
  }

  return (
    <ul>
      {sections.map((section, index) => (
        <li key={index}>
          <a href={section.url}>{section.title}</a>
        </li>
      ))}
    </ul>
  );
}

const DocPage = ({data, documentParam}) => (
  <Layout>
    {DocSectionIndex(`/docs/${documentParam}`, data.sections_total)}
  </Layout>
)

export async function getServerSideProps(ctx) {
  const {params: {document}} = ctx;
  const req = await axios.get(`http://localhost:7687/docs/${document}?verbose=true`);
  const data = req.data;
  delete data.content;
  return {
    props: {
      data,
      documentParam: document,
    },
  };
}

export default DocPage
