import Link from 'next/link'
import Layout from '../../../components/layout'
import Doc from '../../../components/doc'
import axios from 'axios'

const DocSection = props => (
  <Layout>
    <Doc {...props} />
  </Layout>
)

export async function getServerSideProps(ctx) {
  const {params: {document, section}} = ctx;
  const req = await axios.get(`${process.env.apiEndpoint}/docs/${document}/${section}?verbose=true`);
  const doc = req.data;
  return {
    props: {
      doc,
      documentParam: document,
    },
  };
}

export async function getStaticPaths(ctx) {
  const {params: {document}} = ctx;
  const req = await axios.get(`${process.env.apiEndpoint}/docs/${document}?verbose=true`);
  const {sections = []} = req.data;
  const paths = sections.map(section => {
    return {
      params: {
        section: section.section_index,
      },
    };
  });

  return {
    paths,
    fallback: false,
  };
}

export default DocSection
