import Link from 'next/link'
import Layout from '../../../components/layout'
import Doc from '../../../components/doc'
import axios from 'axios'

const DocSectionIndex = (urlPrefix, sections) => {
  return (
    <ul className="section-index-list">
      {sections.map((section, index) => {
        const {section_index, subtitle, title} = section;
        const url = `${urlPrefix}/${section_index}`;

        return (
          <li className="flex justify-space-between" key={index}>
            <a href={url}>{title}</a>
            <span>{subtitle}</span>
          </li>
        );
      })}
    </ul>
  );
}

const DocPage = ({data, documentParam}) => (
  <Layout>
    <h2>{data.title}</h2>
    <p>{data.author}</p>
    <p>{data.description}</p>
    {DocSectionIndex(`/docs/${documentParam}`, data.sections)}
  </Layout>
)

export async function getServerSideProps(ctx) {
  const {params: {document}} = ctx;
  const req = await axios.get(`http://localhost:7687/docs/${document}?verbose=true`);
  const data = req.data;
  delete data.content;
  return {
    props: {
      data,
      documentParam: document,
    },
  };
}

export default DocPage
